      }

  decrement = () => {
    this.props.dispatch({ type: 'DECREMENT' });
        // fill in later
      }
      render() {
        return (
          <div>
            <h2>Counter</h2>
            <div>
              <button onClick={this.decrement}>-</button>
              <span>{this.props.count}</span>
              <button onClick={this.increment}>+</button>
            </div>
          </div>
        )
      }
}
